mapper.select:
SELECT rnum, product_no as productNo, product_name as productName, to_char(product_price , '999,999,999,999,999') as productPrice, img_oname as imgOname, img_sname as imgSname, img_type as imgType, category_no as productCategoryNo, category_name as categoryName FROM ( SELECT rownum as rnum, product_no, product_name, product_price, img_oname, img_sname, img_type, category_no, category_name FROM ( SELECT p.product_no, p.product_name, p.product_price, i.img_oname, i.img_sname, i.img_type, c.category_no, c.category_name
FROM products p JOIN products_img i ON p.product_no = i.product_no AND i.img_state = 1 JOIN product_categories c ON p.product_category_no = c.category_no WHERE p.product_category_no = #{cno} ORDER BY p.product_no asc, p.product_regdate desc
) WHERE rownum <= #{pager.endRowNo} ) WHERE rnum >= #{pager.startRowNo}

mapper.update:
UPDATE
	products
SET
	product_sellcount = product_sellcount + #{amount}
WHERE
	product_no = #{productNo}

UPDATE
	products
SET
	productState = #{product_state}
WHERE
	product_no = #{productNo}

mapper.select:
SELECT count(*)
FROM products
WHERE product_category_no = #{cno}
